import {useState, useEffect} from "react";
import {reactLocalStorage} from 'reactjs-localstorage';
import useFetch from "./useFetch";
import Maps from "./Maps";




const Form = () => {
    const { error, isPending, data: kiosk } = useFetch('http://localhost:8000/kiosk')
    const [logitude, setLogitude] = useState(44);
    const [attitude, setAttitude] = useState(-80);
    
    /*
    useEffect(() => {
        fetch('http://localhost:8000/kiosk')
        .then(res => {
            return res.json();
        })
        .then(data => {
            setKiosks(data);
            console.log(kiosks);
        })
    }, [])*/
    //const { error, isPending, data: kiosks } = useFetch('http://localhost:8000/kiosk');

    const handleSubmit = (e) => {
        //e.preventDefault();  
        reactLocalStorage.set('lat', logitude);
        reactLocalStorage.set('lng', attitude);
      }

    return ( 
        
        <div>
            <Maps lat={reactLocalStorage.get('var', 44)} lng={reactLocalStorage.get('lng', -80)}/>
            <div className="create">
            <h1>Input your location</h1>
            <form onSubmit={handleSubmit}>
                <label>Logitude</label>
                <input
                type="number"
                step="0.0001"
                value={logitude}
                onChange={(e) => setLogitude(e.target.value)}/>
                <label>Attitude</label>
                <input
                type="number"
                step={0.0001}
                value={attitude}
                onChange={(e) => setAttitude(e.target.value)}/>
                <button>Update Location</button>
            </form>
                <h2>Log:{logitude} Att:{attitude}</h2>
            </div>

            { error && <div>{ error }</div> }
            { isPending && <div>Loading...</div> }
            {kiosk && kiosk.map(kiosk => (
                <div>
                <h3>Location of {kiosk.name}</h3>
                <h3>{kiosk.latitude}</h3>
                <h3>{kiosk.longitude}</h3>
                </div>
            ))}
        </div>
     );
}
 
export default Form;
